import matplotlib.pyplot as plt

def plot_path(mission):
    mission_x = []
    mission_y = []
    for element in mission:
        mission_x.append(element[0])
        mission_y.append(element[1])
        
    plt.plot(mission_y, mission_x, label = "Mission")
    plt.legend()
    plt.show()

def plot_mission(mission, odom):
    mission_x = []
    mission_y = []
    for element in mission:
        mission_x.append(element[0])
        mission_y.append(element[1])
        
    odom_x = []
    odom_y = []
    for element in odom:
        mission_x.append(element[0])
        mission_y.append(element[1])
        
    plt.plot(mission_y, mission_x, label = "Mission")
    plt.plot(odom_y,    odom_x,    label = "Odom")
    plt.legend()
    plt.show()
        
